/*
由于苹果后台没法查看具体每单的支付状态，不像支付宝，微信，谷歌，stripe那样有完善的后台，
经常用户反馈会员没加上，
这时候就需要根据用户的订单号或订阅id去查询支付状态
准备工作

登录appstoreconnect.inapps.com/
生成密钥：- 用户和访问 -> 密钥 ->App 内购项目
创建密钥：复制 Issuer ID 和 密钥 ID
下载p8格式的密钥，只能下载一次
转.p8格式为.pem 的密钥, openssl pkcs8 -nocrypt -in ./SubscriptionKey_xx.p8 -out ./sub_pkcs8.pem
*/

type AppStoreApi struct {
	client *apple.Client
	cfg    conf.InApps
}

func NewAppStoreApi() *AppStoreApi {
	cfg := conf.AppConfig.Apple.InApps
	// KID是密钥id，必须与密钥的文件名称匹配，不然容易出错
	privatePath := fmt.Sprintf("deploy/SubscriptionKey_%v.p8", cfg.Kid)
	file, err := os.Open(privatePath)
	if err != nil {
		logger.Fatalf("初始化Apple pay client failed：%v", err.Error())
	}
	defer file.Close()
	//读取文件的内容
	info, _ := file.Stat()
	buf := make([]byte, info.Size())
	_, err = file.Read(buf)
	if err != nil {
		logger.Fatalf("初始化Apple pay client failed：%v", err.Error())
	}
	//pem解码
	//x509解码
	client, err := apple.NewClient(cfg.Iss, cfg.Bid, cfg.Kid, string(buf), cfg.IsProd)
	if err != nil {
		logger.Fatalf("初始化Apple pay client failed: %v", err.Error())
	}
	return &AppStoreApi{
		client: client,
		cfg:    cfg,
	}
}

// GetAppStoreOrder 查询用户订单的收据 orderId 消费者端的订单号id 如 `MQ1MGKZX31`，
func (a *AppStoreApi) GetAppStoreOrder(ctx context.Context, orderId string) (resp *apple.LookUpOrderIdRsp, err error) {
	//orderId := "2000000184445477"
	rsp, err := a.client.LookUpOrderId(ctx, orderId)
	if err != nil {
		logger.Errorf("client.LookUpOrderId(),err:%+v", err)
		return
	}
	/**
		{
	    "status":0,
	    "signedTransactions":[
	        "..HCDFGxo5p1xkl9MWNymeC-K_EMx1P1_3cGsz0-HCybHUvMHRkipie33YZesLZy33PqPL1Nwbb9pkFAtmbvSETQ"
	    ]
	}
	*/
	if len(rsp.SignedTransactions) == 0 {
		logger.Debugf("rsp:%+v", rsp)
	}
	for _, v := range rsp.SignedTransactions {
		transaction, _ := v.DecodeSignedTransaction()
		logger.Debugf("transactions:%+v", transaction)
	}
	return resp, nil
}

// 查询用户最后一次订阅项目状态
func (a *AppStoreApi) GetSubscriptionsStatus(ctx context.Context, originalTransactionId string) (resp apple.AllSubscriptionStatusesRsp, err error) {
	//originalTransactionId := "2000000184445477"

	rsp, err := a.client.GetAllSubscriptionStatuses(ctx, originalTransactionId)
	if err != nil {
		logger.Errorf("client.GetAllSubscriptionStatuses(),err:%+v", err)
		return
	}
	/**
	{
		"appAppleId":0,
		"bundleId":"Com.VoiceRecording.Telephone",
		"environment":"Sandbox",
		"data":[
			{
				"subscriptionGroupIdentifier":"21024612",
				"lastTransactions":[
					{
						"originalTransactionId":"2000000184445477",
						"status":2,
						"signedRenewalInfo":"..sQUCe-J89HzKWeezxdAYeoPbkoD1V2Hiso0efmKOUBmAss9zbVwHhRv3DrsNipNji0tziWNTMTBpcnzKYCV7mQ",
						"signedTransactionInfo":"..fqpwflhDDRPMBAPlmhBoCVP_wnjsm7xb78ydUctdd3TUSAlpANw_djPlxg-RcGdY6zRkyE0OeoDwC6Soi2NDJQ"
					}
				]
			}
		]
	}
	*/
	if len(rsp.Data) == 0 {
		logger.Debugf("rsp:%+v", rsp)
	}
	for _, dataItem := range rsp.Data {
		for _, lastTransaction := range dataItem.LastTransactions {
			ti, _ := lastTransaction.DecodeTransactionInfo()
			logger.Debugf("TransactionInfo:%+v", ti)

			ri, _ := lastTransaction.DecodeRenewalInfo()
			logger.Debugf("RenewalInfo:%+v", ri)

		}
	}
	return resp, nil
}

// 验证交易
func (a *AppStoreApi) VerifyReceipt(ctx context.Context, receipt, bundleId string) (res *apple.VerifyResponse, err error) {
	/*
		对于需要发送到 Apple 服务器进行验证的回执，你应该按照 Apple 的推荐策略进行：
		先发送请求到生产环境，如果生产环境返回 21007 错误，那么再发送请求到沙盒环境。
		这是因为一个应用可能在测试环境中购买一个内购项目，然后在生产环境中恢复这个购买。
		在这种情况下，你的服务器会收到一个沙盒环境的回执。
	*/
	testReceiptCode := 21007
	rsp, err := apple.VerifyReceipt(ctx, apple.UrlProd, a.cfg.Password, receipt)

	if rsp != nil && rsp.Status == testReceiptCode {
		// 沙盒环境
		rsp, err = apple.VerifyReceipt(ctx, apple.UrlSandbox, a.cfg.Password, receipt)
	}

	if err != nil {
		return res, err
	}

	// result.Receipt 发送以供验证的收据的 JSON 表示形式。
	if len(rsp.Receipt.InApp) <= 0 {
		return res, errors.New("Empty receipt")
	}
	if rsp.Receipt.BundleId != bundleId {
		return res, errors.New("Invalid bundle id")
	}
	/**
	{
	    "receipt":{
	        "original_purchase_date_pst":"2021-08-14 05:28:17 America/Los_Angeles",
	        "purchase_date_ms":"1628944097586",
	        "unique_identifier":"13f339a765b706f8775f729723e9b889b0cbb64e",
	        "original_transaction_id":"1000000859439868",
	        "bvrs":"10",
	        "transaction_id":"1000000859439868",
	        "quantity":"1",
	        "in_app_ownership_type":"PURCHASED",
	        "unique_vendor_identifier":"6DFDEA8B-38CE-4710-A1E1-BAEB8B66FEBD",
	        "item_id":"1581250870",
	        "version_external_identifier":"0",
	        "bid":"com.huochai.main",
	        "is_in_intro_offer_period":"false",
	        "product_id":"10002",
	        "purchase_date":"2021-08-14 12:28:17 Etc/GMT",
	        "is_trial_period":"false",
	        "purchase_date_pst":"2021-08-14 05:28:17 America/Los_Angeles",
	        "original_purchase_date":"2021-08-14 12:28:17 Etc/GMT",
	        "original_purchase_date_ms":"1628944097586"
	    },
	    "status":0
	}
	*/
	//if rsp.Receipt != nil {
	//	logger.Infof("receipt:%+v", rsp.Receipt)
	//}
	return rsp, nil
}

