func (a *AppStoreApi) GetTransactionInfo(ctx context.Context, transactionId string) (rsp *apple.TransactionInfo, err error) {
	res, err := a.client.GetTransactionInfo(ctx, transactionId)
	if err != nil {
		return nil, err
	}
	if res.SignedTransactionInfo == "" {
		return nil, fmt.Errorf("signedTransactionInfo is empty")
	}
	var ti apple.TransactionInfo
	if err = apple.ExtractClaims(res.SignedTransactionInfo, &ti); err != nil {
		return nil, err
	}
	return &ti, nil
}
